<script>
export default {
  computed: {
    articleTitle () {
      switch (this.$route.fullPath) {
        case '/article/blog':
          return 'Code Library & Blog'
        case '/article/algorithm':
          return 'Mathematics & Algorithm'
        case '/article/app':
          return 'APP Laboratory'
        default:
          return decodeURIComponent(this.$route.fullPath).match(/^\/article\/(?:(?:algorithm)|(?:blog)|(?:app))\/(.*$)/)[1]
      }
    }
  }
}
